mapper.select:
select count(*)
from c_dic_client


		is_active = '1'


		AND type = #{type, jdbcType=CHAR}


		AND name LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')


		AND name = #{name, jdbcType=VARCHAR}

select
,
	(case type when '0' then '原告' when '1' then '被告' when '1' then '第三人' else '法定代表人' end ) as type_name
from c_dic_client


		is_active = '1'


		AND type = #{type, jdbcType=CHAR}


		AND name LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')


order by ${sortItem} ${sortOrder}
limit
#{startNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

mapper.insert:
insert into c_dic_client (id, type, name, 
  is_active, create_time, create_id
  )
values (#{id,jdbcType=INTEGER}, #{type,jdbcType=CHAR}, #{name,jdbcType=VARCHAR}, 
  #{isActive,jdbcType=CHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createId,jdbcType=INTEGER}
  )
