mapper.select:
select count(*)
from c_dic_lawyer


		is_active = '1'


		lawfirm_id = #{lawfirmId, jdbcType=INTEGER}


		AND name LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')


		AND name = #{name, jdbcType=VARCHAR}

mapper.insert:
insert into c_dic_lawyer (id, lawfirm_id, name, 
  is_active, create_time, create_id
  )
values (#{id,jdbcType=INTEGER}, #{lawfirmId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{isActive,jdbcType=CHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createId,jdbcType=INTEGER}
  )
